import React from 'react';
import { useSelector } from 'react-redux';
import CryptoRow from './CryptoRow';

const CryptoTable = () => {
  const assets = useSelector(state => state.crypto.assets);

  // Default to an empty array if assets is not defined or not an array
  const assetsList = Array.isArray(assets) ? assets : [];

  if (assetsList.length === 0) {
    return <div>Loading or No Data Available</div>;
  }

  return (
    <div className="table-container">
      <table>
        <thead>
          <tr>
            <th>Logo</th><th>Name</th><th>Symbol</th><th>Price</th><th>1h %</th>
            <th>24h %</th><th>7d %</th><th>Market Cap</th><th>Volume(24h)</th>
            <th>Circulating</th><th>Max Supply</th><th>Last 7 Days</th>
          </tr>
        </thead>
        <tbody>
          {assetsList.map(asset => <CryptoRow key={asset.id} asset={asset} />)}
        </tbody>
      </table>
    </div>
  );
};

export default CryptoTable;
