import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// Register necessary chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function CryptoTable({ data }) {
  const generateChartData = (chartData7d) => {
    return {
      labels: ['Day 1', 'Day 2', 'Day 3', 'Day 4', 'Day 5', 'Day 6', 'Day 7'],
      datasets: [
        {
          label: 'Price Over Last 7 Days',
          data: chartData7d,
          fill: false,
          borderColor: '#4caf50', // Line color
          tension: 0.1,
        },
      ],
    };
  };

  return (
    <div className="crypto-table">
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>24h Change</th>
            <th>7D Change</th>
            <th>Market Cap</th>
            <th>Last 7 Days</th>
          </tr>
        </thead>
        <tbody>
          {data.map((crypto) => (
            <tr key={crypto.id}>
              <td>{crypto.name}</td>
              <td>${crypto.price.toFixed(2)}</td>
              <td>{crypto.percentChange24h}%</td>
              <td>{crypto.percentChange7d}%</td>
              <td>${crypto.marketCap.toLocaleString()}</td>
              <td>
                <div style={{ width: '200px', height: '120px' }}>
                  {/* Chart visualization for the last 7 days */}
                  <Line data={generateChartData(crypto.chartData7d)} />
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default CryptoTable;
