// src/utils/mockWebSocket.js
import sampleData from './sampleData';

export function connectMockWebSocket(onMessage) {
  let index = 0;

  const interval = setInterval(() => {
    if (index < sampleData.length) {
      onMessage({ data: JSON.stringify(sampleData[index]) });
      index++;
    } else {
      index = 0;
    }
  }, 2000);

  return () => clearInterval(interval);
}
