// src/hooks/useCryptoData.js
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { setCryptoData } from '../features/crypto/cryptoSlice';
import { connectMockWebSocket } from '../utils/mockWebSocket';

const useCryptoData = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    const unsubscribe = connectMockWebSocket((event) => {
      const newData = JSON.parse(event.data);
      dispatch(setCryptoData([newData])); // Simplified for demo
    });

    return () => unsubscribe();
  }, [dispatch]);
};

export default useCryptoData;
